import React, { useState } from 'react';

function App() {
  const [showMessage, setShowMessage] = useState(false);

  const handleButtonClick = () => {
    setShowMessage(true);
  };

  return (
    <div>
      <button onClick={handleButtonClick}>Press Me</button>
      {showMessage && <p>The button was pressed!</p>}
    </div>
  );
}

