import React, { useState } from 'react';
import './home.css';

function Manage_Container({ environment }) {
    const [showMessage, setShowMessage] = useState(false);
    const [message, setMessage] = useState('');

    const getActionUrl = (action) => {
        if (environment === 'Development') {
            return action === 'start' ? 'http://development-url/start' : 'http://development-url/stopcontainer';
        } else if (environment === 'Testing') {
            return action === 'start' ? 'http://testing-url/start' : 'http://testing-url/stopcontainer';
        } else if (environment === 'Staging') {
            return action === 'start' ? 'http://staging-url/start' : 'http://staging-url/stopcontainer';
        } else if (environment === 'Production') {
            return action === 'start' ? 'http://production-url/start' : 'http://production-url/stopcontainer';
        } else {
            return '';
        }
    };

    const handleClick = (action) => {
        const url = getActionUrl(action);
        fetch(url, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ action })
        })
        .then(response => {
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            // Handle successful response here
        })
        .catch(error => {
            console.error('There was a problem with your fetch operation:', error);
            // Handle error here
        });
    };

    return (
        <div className="container">
            <img src="https://upload.wikimedia.org/wikipedia/commons/thumb/c/cd/BT_logo_2019.svg/900px-BT_logo_2019.svg.png" alt="BT Logo" style={{ maxWidth: "100px", marginBottom: "20px" }} />
            <h1 className="title">Home Page</h1>
            <ul>
                <li>
                    <button onClick={() => handleClick('start')}>Start Containers</button>
                </li>
                <li>
                    <button onClick={() => handleClick('stop')}>Stop Containers</button>
                </li>
            </ul>
            {showMessage && <p>{message}</p>}
            <div className="Home">
                <a href="/Home"><i className="fas fa-sign-out-alt"></i> Home</a>
            </div>
        </div>
    );
}

export default Manage_Container;
