import {  useState } from "react";
import axios from "axios";
import { useNavigate } from 'react-router-dom';

function Register() {
  
    const [employeename, setEmployeename] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const navigate = useNavigate();


    async function save(event) {
        event.preventDefault();
        try {
          const response = await axios.post("http://localhost:8085/api/v1/employee/save", {
              employeename: employeename,
              email: email,
              password: password,
          });
          console.log(response.data);
          alert("Employee Registration Successful");
          navigate('/Login');
      } catch (error) {
          if (error.response && error.response.data && error.response.data.message === "Email already exists") {
              alert("Email already exists");
          } else {
              console.error(error);
              alert("An error occurred while registering the employee. Please try again.");
          }
        }
      }
  
    return (
    <div>
    <div class="container mt-4" >
    <div class="card">
            <h1>Registation</h1>
    
    <form>
        <div class="form-group">
          <label>Employee name</label>
          <input type="text"  class="form-control" id="employeename" placeholder="Enter Name"
          
          value={employeename}
          onChange={(event) => {
            setEmployeename(event.target.value);
          }}
          />

        </div>

        <div class="form-group">
          <label>email</label>
          <input type="email"  class="form-control" id="email" placeholder="Enter Email"
          
          value={email}
          onChange={(event) => {
            setEmail(event.target.value);
          }}
          
          />
 
        </div>

        <div class="form-group">
            <label>password</label>
            <input type="password"  class="form-control" id="password" placeholder="Enter password"
            
            value={password}
            onChange={(event) => {
              setPassword(event.target.value);
            }}
            
            />
          </div>

        <div className="btn-group">
        <button type="submit" class="btn btn-primary mt-4" onClick={save}>Register</button>
        <button className="btn btn-primary" onClick={() => navigate('/Login')}>Login</button>
        </div>
       
      </form>
    </div>
    </div>
     </div>
    );
  }
  
  export default Register;
