// ListImages.jsx

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

function ListImages() {
  const [images, setImages] = useState([]);
  const history = useHistory();

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get('/api/v1/employee/images');
      if (response.status === 200) {
        const imagesData = response.data;
        // Sort images by repository name
        imagesData.sort((a, b) => a[0].repositoryName.localeCompare(b[0].repositoryName));
        // Sort and mark new images within each repository
        imagesData.forEach((repository) => {
          repository.sort((a, b) => new Date(b.imagePushedAt) - new Date(a.imagePushedAt));
          repository.forEach((image) => {
            image.isNew = isNewImage(image.imagePushedAt);
          });
        });
        setImages(imagesData);
      } else {
        // Handle error response
        console.error('Error fetching images:', response.statusText);
        history.push('/dashboard'); // Redirect to dashboard on error
      }
    } catch (error) {
      // Handle request error
      console.error('Error fetching images:', error);
      history.push('/dashboard'); // Redirect to dashboard on error
    }
  };

  const isNewImage = (pushedAt) => {
    // Add logic to determine if the image is new based on pushedAt timestamp
    // Example logic: Compare pushedAt timestamp with a threshold date to determine if it's new
    return false;
  };

  return (
    <div>
      <h1>List of Images</h1>
      {images.map((repository, index) => (
        <div key={index}>
          <h2>Service: {repository[0].repositoryName}</h2>
          {repository.map((image, index) => (
            <div key={index} className={image.isNew ? 'image-card new' : 'image-card'}>
              <span className="tag">Tag:</span> {image.imageTag}{' '}
              <span className="repository-name">Repository: {image.repositoryName}</span> -{' '}
              <span className="pushed-at">Pushed At: {image.imagePushedAt}</span>
            </div>
          ))}
        </div>
      ))}
    </div>
  );
}

export default ListImages;
