import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './list_images.css';

const ListImages = () => {
    const [images, setImages] = useState([]);

    useEffect(() => {
        const fetchImages = async () => {
            try {
                const response = await axios.get('http://localhost:8085/api/v1/employee/images');
                setImages(response.data);
            } catch (error) {
                console.error('Error fetching images:', error);
            }
        };

        fetchImages();
    }, []);

    return (
        <div className="container">
            <h1>Profile Hub Deployment History</h1>
            {images.map((repository, index) => (
                <div key={index}>
                    <h2>Service: {repository[0].repositoryName}</h2>
                    {repository.map((image, idx) => (
                        <div key={idx} className={`image-card ${image.isNew ? 'new' : ''}`}>
                            <span className="tag">Tag:</span> {image.imageTag} <span className="repository-name">Repository: {image.repositoryName}</span> - <span className="pushed-at">Pushed At: {image.imagePushedAt}</span>
                        </div>
                    ))}
                </div>
            ))}
            <a href="/dashboard" className="back-link"><i className="fas fa-arrow-left"></i> Back to Dashboard</a>
        </div>
    );
};

export default ListImages;
